Objective: Add new tables `googleIntegrations` and `contacts` to the database schema to store OAuth tokens and user contacts.

File to be modified: `C:\Users\Administrator\Documents\SSDI\shared\schema.ts`

**Instructions:**

1.  Import necessary functions from `drizzle-orm/pg-core`.
2.  Define the `googleIntegrations` table to store OAuth tokens. The `refreshToken` will be stored as encrypted text.
3.  Define the `contacts` table to store user's contacts, linked by `userId`.
4.  Define Zod schemas for insertion and selection for the new tables.

```typescript
import {
  pgTable,
  serial,
  integer,
  varchar,
  text,
  boolean,
  timestamp,
  pgEnum,
  jsonb,
  real,
  index,
} from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema, createSelectSchema } from 'drizzle-zod';
import { z } from 'zod';

// USER
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: varchar("username", { length: 256 }).unique().notNull(),
  password: text("password").notNull(),
  name: varchar("name", { length: 256 }),
  applicationId: varchar("applicationId", { length: 256 }),
});

export const usersRelations = relations(users, ({ many }) => ({
	sections: many(sections),
  retirementTrackings: many(retirementTracking),
  googleIntegrations: many(googleIntegrations),
  contacts: many(contacts),
}));

export type User = typeof users.$inferSelect;
export type InsertUser = typeof users.$inferInsert;
export const insertUserSchema = createInsertSchema(users);
export const selectUserSchema = createSelectSchema(users);

// GOOGLE INTEGRATIONS
export const googleIntegrations = pgTable("google_integrations", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  email: varchar("email", { length: 256 }).notNull(),
  accessToken: text("access_token").notNull(),
  refreshToken: text("refresh_token").notNull(), // This will be encrypted
  expiryDate: timestamp("expiry_date", { withTimezone: true }).notNull(),
  scopes: text("scopes").notNull(), // Store scopes as a space-separated string
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow().notNull(),
}, (table) => {
  return {
    userIdx: index("google_integrations_user_id_idx").on(table.userId),
  };
});

export const googleIntegrationsRelations = relations(googleIntegrations, ({ one }) => ({
  user: one(users, {
    fields: [googleIntegrations.userId],
    references: [users.id],
  }),
}));

export type GoogleIntegration = typeof googleIntegrations.$inferSelect;
export type InsertGoogleIntegration = typeof googleIntegrations.$inferInsert;
export const insertGoogleIntegrationSchema = createInsertSchema(googleIntegrations);
export const selectGoogleIntegrationSchema = createSelectSchema(googleIntegrations);

// CONTACTS
export const contacts = pgTable("contacts", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  name: varchar("name", { length: 256 }).notNull(),
  role: varchar("role", { length: 256 }),
  email: varchar("email", { length: 256 }).notNull(),
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow().notNull(),
}, (table) => {
  return {
    userIdx: index("contacts_user_id_idx").on(table.userId),
  };
});

export const contactsRelations = relations(contacts, ({ one }) => ({
  user: one(users, {
    fields: [contacts.userId],
    references: [users.id],
  }),
}));

export type Contact = typeof contacts.$inferSelect;
export type InsertContact = typeof contacts.$inferInsert;
export const insertContactSchema = createInsertSchema(contacts);
export const selectContactSchema = createSelectSchema(contacts);


// SECTIONS
export const sectionStatusEnum = pgEnum("section_status", ["complete", "in-progress", "needs-attention"]);

export const sections = pgTable("sections", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  name: varchar("name", { length: 256 }).notNull(),
  description: text("description"),
  status: sectionStatusEnum("status").notNull(),
  order: integer("order").notNull(),
});

export const sectionsRelations = relations(sections, ({ one, many }) => ({
  user: one(users, {
    fields: [sections.userId],
    references: [users.id],
  }),
  documents: many(documents),
}));

export type Section = typeof sections.$inferSelect;
export type InsertSection = typeof sections.$inferInsert;
export const insertSectionSchema = createInsertSchema(sections);
export const selectSectionSchema = createSelectSchema(sections);


// DOCUMENTS
export const documentStatusEnum = pgEnum("document_status", ["uploaded", "pending", "missing"]);
export const documentCategoryEnum = pgEnum("document_category", ["personal", "medical", "legal", "employment", "government"]);

export const documents = pgTable("documents", {
  id: serial("id").primaryKey(),
  sectionId: integer("section_id").notNull().references(() => sections.id, { onDelete: 'cascade' }),
  name: varchar("name", { length: 256 }).notNull(),
  description: text("description"),
  fileName: text("file_name"),
  fileSize: integer("file_size"),
  status: documentStatusEnum("status").notNull(),
  uploadedAt: timestamp("uploaded_at", { withTimezone: true }),
  contactInfo: text("contact_info"),
  notes: text("notes"),
  category: documentCategoryEnum("category").notNull(),
});

export const documentsRelations = relations(documents, ({ one }) => ({
  section: one(sections, {
    fields: [documents.sectionId],
    references: [sections.id],
  }),
}));

export type Document = typeof documents.$inferSelect;
export type InsertDocument = typeof documents.$inferInsert;
export const insertDocumentSchema = createInsertSchema(documents);
export const selectDocumentSchema = createSelectSchema(documents);


// RETIREMENT TRACKING
export const communicationTypeEnum = pgEnum("communication_type", ["email", "letter", "phone_call", "online_message", "deadline", "appointment"]);
export const sourceEnum = pgEnum("source", ["social_security", "ssa_gov", "phone", "mail", "email"]);
export const priorityEnum = pgEnum("priority", ["high", "medium", "low"]);

export const retirementTracking = pgTable("retirement_tracking", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  type: communicationTypeEnum("type").notNull(),
  title: varchar("title", { length: 256 }).notNull(),
  description: text("description").notNull(),
  receivedAt: timestamp("received_at", { withTimezone: true }).notNull(),
  source: sourceEnum("source").notNull(),
  priority: priorityEnum("priority").notNull(),
  isActionRequired: boolean("is_action_required").default(false).notNull(),
  actionDeadline: timestamp("action_deadline", { withTimezone: true }),
  notes: text("notes"),
  attachmentFileName: text("attachment_file_name"),
  attachmentFileSize: integer("attachment_file_size"),
  createdAt: timestamp("created_at", { withTimezone: true }).defaultNow().notNull(),
});

export const retirementTrackingRelations = relations(retirementTracking, ({ one }) => ({
  user: one(users, {
    fields: [retirementTracking.userId],
    references: [users.id],
  }),
}));

export type RetirementTracking = typeof retirementTracking.$inferSelect;
export type InsertRetirementTracking = typeof retirementTracking.$inferInsert;
export const insertRetirementTrackingSchema = createInsertSchema(retirementTracking);
export const selectRetirementTrackingSchema = createSelectSchema(retirementTracking);
```

5.  After saving the schema file, run the following command in your terminal to apply the changes to your database:
    ```bash
    npm run db:push
    ```

Objective: Centralize all logic for Google API interactions and data encryption into dedicated service files. This improves code organization and reusability.

File to be created: `C:\Users\Administrator\Documents\SSDI\server\encryption.ts`

**Instructions:**

1.  Create this new file to handle encryption and decryption of the refresh tokens.

```typescript
import crypto from 'crypto';

const ALGORITHM = 'aes-256-cbc';
const IV_LENGTH = 16;
const ENCRYPTION_KEY = Buffer.from(process.env.ENCRYPTION_SECRET!, 'utf-8');

if (!process.env.ENCRYPTION_SECRET || process.env.ENCRYPTION_SECRET.length !== 32) {
  throw new Error('ENCRYPTION_SECRET must be set and be 32 characters long.');
}

export function encrypt(text: string): string {
  const iv = crypto.randomBytes(IV_LENGTH);
  const cipher = crypto.createCipheriv(ALGORITHM, ENCRYPTION_KEY, iv);
  let encrypted = cipher.update(text, 'utf8', 'hex');
  encrypted += cipher.final('hex');
  return iv.toString('hex') + ':' + encrypted;
}

export function decrypt(text: string): string {
  const textParts = text.split(':');
  const iv = Buffer.from(textParts.shift()!, 'hex');
  const encryptedText = Buffer.from(textParts.join(':'), 'hex');
  const decipher = crypto.createDecipheriv(ALGORITHM, ENCRYPTION_KEY, iv);
  let decrypted = decipher.update(encryptedText, 'hex', 'utf8');
  decrypted += decipher.final('utf8');
  return decrypted;
}
```

File to be created: `C:\Users\Administrator\Documents\SSDI\server\google.ts`

**Instructions:**

1.  Create this new service file. It will contain all methods for interacting with Google APIs.

```typescript
import { google } from 'googleapis';
import { storage } from './storage';
import { decrypt } from './encryption';
import nodemailer from 'nodemailer';
import { Document } from '@shared/schema';
import fs from 'fs';
import path from 'path';

const oauth2Client = new google.auth.OAuth2(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  process.env.GOOGLE_REDIRECT_URI
);

export const googleAuthUrl = oauth2Client.generateAuthUrl({
  access_type: 'offline',
  scope: [
    'https://www.googleapis.com/auth/calendar.events',
    'https://www.googleapis.com/auth/gmail.send'
  ],
  prompt: 'consent',
});

export async function getTokensFromCode(code: string) {
  const { tokens } = await oauth2Client.getToken(code);
  return tokens;
}

async function getAuthenticatedClient(userId: number) {
  const integration = await storage.getGoogleIntegration(userId);
  if (!integration) {
    throw new Error('Google integration not found for this user.');
  }

  const client = new google.auth.OAuth2(
    process.env.GOOGLE_CLIENT_ID,
    process.env.GOOGLE_CLIENT_SECRET,
    process.env.GOOGLE_REDIRECT_URI
  );

  const tokens = {
    access_token: integration.accessToken,
    refresh_token: decrypt(integration.refreshToken),
    expiry_date: new Date(integration.expiryDate).getTime(),
    token_type: 'Bearer',
  };

  client.setCredentials(tokens);

  // Auto-refresh token if needed
  client.on('tokens', async (newTokens) => {
    let updatedTokens = { ...tokens, ...newTokens };

    // Google doesn't always send a new refresh token
    if (!newTokens.refresh_token) {
        newTokens.refresh_token = tokens.refresh_token;
    }

    await storage.updateGoogleIntegration(userId, {
        accessToken: newTokens.access_token!,
        refreshToken: newTokens.refresh_token!, // This will be re-encrypted by storage layer
        expiryDate: new Date(newTokens.expiry_date!),
    });

    client.setCredentials(updatedTokens);
  });

  // Check if token is expired and refresh if necessary
  if (new Date() > integration.expiryDate) {
    const { credentials } = await client.refreshAccessToken();
    client.setCredentials(credentials);
  }

  return client;
}

export async function createCalendarEvent(userId: number, event: { summary: string; description: string; start: { dateTime: string; timeZone: string; }; end: { dateTime: string; timeZone: 'America/Los_Angeles'; }; }) {
    try {
        const auth = await getAuthenticatedClient(userId);
        const calendar = google.calendar({ version: 'v3', auth });

        await calendar.events.insert({
            calendarId: 'primary',
            requestBody: event,
        });
        return { success: true };
    } catch (error) {
        console.error('Failed to create calendar event:', error);
        return { success: false, error: (error as Error).message };
    }
}

export async function sendEmail(userId: number, to: string, subject: string, body: string, attachments: Document[]) {
    try {
        const auth = await getAuthenticatedClient(userId);
        const integration = await storage.getGoogleIntegration(userId);
        if (!integration) throw new Error("User not integrated with Google");

        const transporter = nodemailer.createTransport({
            service: 'gmail',
            auth: {
                type: 'OAuth2',
                user: integration.email,
                clientId: process.env.GOOGLE_CLIENT_ID,
                clientSecret: process.env.GOOGLE_CLIENT_SECRET,
                refreshToken: decrypt(integration.refreshToken),
                accessToken: integration.accessToken,
            },
        });

        const mailOptions = {
            from: integration.email,
            to: to,
            subject: subject,
            html: body,
            attachments: attachments.map(doc => {
              const filePath = path.join(process.cwd(), 'uploads', doc.fileName!);
              return {
                filename: doc.name + path.extname(doc.fileName!),
                content: fs.createReadStream(filePath),
              }
            })
        };

        await transporter.sendMail(mailOptions);
        return { success: true };
    } catch (error) {
        console.error('Failed to send email:', error);
        return { success: false, error: (error as Error).message };
    }
}

export async function getUserInfo(tokens: { access_token: string }) {
    const oauth2 = google.oauth2({ version: 'v2', auth: oauth2Client });
    oauth2Client.setCredentials(tokens);
    const { data } = await oauth2.userinfo.get();
    return data;
}
```

Objective: Implement methods in the `DatabaseStorage` class to perform CRUD operations on the new `googleIntegrations` and `contacts` tables.

File to be modified: `C:\Users\Administrator\Documents\SSDI\server\storage.ts`

**Instructions:**

1.  Import the new tables and types from `shared/schema.ts`.
2.  Import the encryption/decryption functions.
3.  Add the new interface methods to `IStorage`.
4.  Implement the new methods in the `DatabaseStorage` class. Note the automatic encryption of the refresh token.

```typescript
import { users, sections, documents, retirementTracking, googleIntegrations, contacts, type User, type InsertUser, type Section, type InsertSection, type Document, type InsertDocument, type RetirementTracking, type InsertRetirementTracking, type GoogleIntegration, type InsertGoogleIntegration, type Contact, type InsertContact } from "@shared/schema";
import { db } from "./db";
import { eq, desc } from "drizzle-orm";
import { encrypt } from "./encryption";

export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Section operations
  getSectionsByUserId(userId: number): Promise<Section[]>;
  getSection(id: number): Promise<Section | undefined>;
  createSection(section: InsertSection): Promise<Section>;
  updateSectionStatus(id: number, status: string): Promise<Section | undefined>;

  // Document operations
  getDocumentsBySectionId(sectionId: number): Promise<Document[]>;
  getDocument(id: number): Promise<Document | undefined>;
  createDocument(document: InsertDocument): Promise<Document>;
  updateDocument(id: number, document: Partial<InsertDocument>): Promise<Document | undefined>;
  deleteDocument(id: number): Promise<boolean>;

  // Retirement tracking operations
  getRetirementTrackingByUserId(userId: number): Promise<RetirementTracking[]>;
  getRetirementTracking(id: number): Promise<RetirementTracking | undefined>;
  createRetirementTracking(tracking: InsertRetirementTracking): Promise<RetirementTracking>;
  updateRetirementTracking(id: number, tracking: Partial<InsertRetirementTracking>): Promise<RetirementTracking | undefined>;
  deleteRetirementTracking(id: number): Promise<boolean>;

  // Google Integration operations
  getGoogleIntegration(userId: number): Promise<GoogleIntegration | undefined>;
  createOrUpdateGoogleIntegration(integration: InsertGoogleIntegration): Promise<GoogleIntegration>;
  updateGoogleIntegration(userId: number, integration: Partial<InsertGoogleIntegration>): Promise<GoogleIntegration | undefined>;
  deleteGoogleIntegration(userId: number): Promise<boolean>;

  // Contact operations
  getContactsByUserId(userId: number): Promise<Contact[]>;
  createContact(contact: InsertContact): Promise<Contact>;
  updateContact(id: number, contact: Partial<InsertContact>): Promise<Contact | undefined>;
  deleteContact(id: number): Promise<boolean>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    // Initialize with sample data if needed
    this.initializeSampleData();
  }

  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getSectionsByUserId(userId: number): Promise<Section[]> {
    return await db.select().from(sections).where(eq(sections.userId, userId)).orderBy(sections.order);
  }

  async getSection(id: number): Promise<Section | undefined> {
    const [section] = await db.select().from(sections).where(eq(sections.id, id));
    return section || undefined;
  }

  async createSection(section: InsertSection): Promise<Section> {
    const [newSection] = await db
      .insert(sections)
      .values(section)
      .returning();
    return newSection;
  }

  async updateSectionStatus(id: number, status: string): Promise<Section | undefined> {
    const [updatedSection] = await db
      .update(sections)
      .set({ status })
      .where(eq(sections.id, id))
      .returning();
    return updatedSection || undefined;
  }

  async getDocumentsBySectionId(sectionId: number): Promise<Document[]> {
    return await db.select().from(documents).where(eq(documents.sectionId, sectionId));
  }

  async getDocument(id: number): Promise<Document | undefined> {
    const [document] = await db.select().from(documents).where(eq(documents.id, id));
    return document || undefined;
  }

  async createDocument(document: InsertDocument): Promise<Document> {
    const documentToInsert = {
      ...document,
      uploadedAt: document.status === 'uploaded' ? new Date() : null
    };

    const [newDocument] = await db
      .insert(documents)
      .values(documentToInsert)
      .returning();
    return newDocument;
  }

  async updateDocument(id: number, documentUpdate: Partial<InsertDocument>): Promise<Document | undefined> {
    const updateData = {
      ...documentUpdate,
      uploadedAt: documentUpdate.status === 'uploaded' ? new Date() : undefined
    };

    const [updatedDocument] = await db
      .update(documents)
      .set(updateData)
      .where(eq(documents.id, id))
      .returning();
    return updatedDocument || undefined;
  }

  async deleteDocument(id: number): Promise<boolean> {
    const result = await db.delete(documents).where(eq(documents.id, id));
    return (result.rowCount || 0) > 0;
  }

  async getRetirementTrackingByUserId(userId: number): Promise<RetirementTracking[]> {
    return await db.select().from(retirementTracking).where(eq(retirementTracking.userId, userId)).orderBy(desc(retirementTracking.receivedAt));
  }

  async getRetirementTracking(id: number): Promise<RetirementTracking | undefined> {
    const [tracking] = await db.select().from(retirementTracking).where(eq(retirementTracking.id, id));
    return tracking || undefined;
  }

  async createRetirementTracking(tracking: InsertRetirementTracking): Promise<RetirementTracking> {
    const [newTracking] = await db
      .insert(retirementTracking)
      .values(tracking)
      .returning();
    return newTracking;
  }

  async updateRetirementTracking(id: number, trackingUpdate: Partial<InsertRetirementTracking>): Promise<RetirementTracking | undefined> {
    const [updatedTracking] = await db
      .update(retirementTracking)
      .set(trackingUpdate)
      .where(eq(retirementTracking.id, id))
      .returning();
    return updatedTracking || undefined;
  }

  async deleteRetirementTracking(id: number): Promise<boolean> {
    const result = await db.delete(retirementTracking).where(eq(retirementTracking.id, id));
    return (result.rowCount || 0) > 0;
  }

  // Google Integration Methods
  async getGoogleIntegration(userId: number): Promise<GoogleIntegration | undefined> {
    const [integration] = await db.select().from(googleIntegrations).where(eq(googleIntegrations.userId, userId));
    return integration || undefined;
  }

  async createOrUpdateGoogleIntegration(integration: InsertGoogleIntegration): Promise<GoogleIntegration> {
    const encryptedIntegration = {
      ...integration,
      refreshToken: encrypt(integration.refreshToken),
    };

    const [newIntegration] = await db.insert(googleIntegrations)
      .values(encryptedIntegration)
      .onConflictDoUpdate({ 
        target: googleIntegrations.userId, 
        set: {
          email: encryptedIntegration.email,
          accessToken: encryptedIntegration.accessToken,
          refreshToken: encryptedIntegration.refreshToken,
          expiryDate: encryptedIntegration.expiryDate,
          scopes: encryptedIntegration.scopes,
        }
      })
      .returning();
    return newIntegration;
  }

  async updateGoogleIntegration(userId: number, integrationUpdate: Partial<InsertGoogleIntegration>): Promise<GoogleIntegration | undefined> {
    let updateData = { ...integrationUpdate };
    if (integrationUpdate.refreshToken) {
      updateData.refreshToken = encrypt(integrationUpdate.refreshToken);
    }

    const [updatedIntegration] = await db
      .update(googleIntegrations)
      .set(updateData)
      .where(eq(googleIntegrations.userId, userId))
      .returning();
    return updatedIntegration || undefined;
  }

  async deleteGoogleIntegration(userId: number): Promise<boolean> {
    const result = await db.delete(googleIntegrations).where(eq(googleIntegrations.userId, userId));
    return (result.rowCount || 0) > 0;
  }

  // Contact Methods
  async getContactsByUserId(userId: number): Promise<Contact[]> {
    return await db.select().from(contacts).where(eq(contacts.userId, userId)).orderBy(desc(contacts.createdAt));
  }

  async createContact(contact: InsertContact): Promise<Contact> {
    const [newContact] = await db.insert(contacts).values(contact).returning();
    return newContact;
  }

  async updateContact(id: number, contactUpdate: Partial<InsertContact>): Promise<Contact | undefined> {
    const [updatedContact] = await db
      .update(contacts)
      .set(contactUpdate)
      .where(eq(contacts.id, id))
      .returning();
    return updatedContact || undefined;
  }

  async deleteContact(id: number): Promise<boolean> {
    const result = await db.delete(contacts).where(eq(contacts.id, id));
    return (result.rowCount || 0) > 0;
  }

  private async initializeSampleData() {
    // Check if user already exists
    const existingUser = await this.getUser(1);
    if(existingUser) return;

    // Create sample user
    const user = await this.createUser({
      username: "john.smith",
      password: "password",
      name: "John Smith",
      applicationId: "SS-2024-001234"
    });

    // Create initial sections
    const sectionsData = [
      {
        userId: user.id,
        name: "Initial Application Documents",
        description: "Basic information to start your application",
        status: "complete",
        order: 1
      },
      {
        userId: user.id,
        name: "Medical Evidence",
        description: "Documents that prove your disability",
        status: "in-progress",
        order: 2
      },
      {
        userId: user.id,
        name: "Work History Documentation",
        description: "Proof of your past employment and job duties",
        status: "complete",
        order: 3
      },
      {
        userId: user.id,
        name: "Appeals Process Documents",
        description: "Documents needed if your application is denied",
        status: "needs-attention",
        order: 4
      }
    ];

    for (const sectionData of sectionsData) {
      const section = await this.createSection(sectionData);

      // Create sample documents for each section
      if (section.name === "Initial Application Documents") {
        await this.createDocument({
          sectionId: section.id,
          name: "Birth Certificate",
          description: "Proves your age and citizenship status",
          fileName: "birth_certificate.pdf",
          fileSize: 2048000,
          status: "uploaded",
          notes: "Official copy from vital records office",
          category: "government"
        });

        await this.createDocument({
          sectionId: section.id,
          name: "W-2 Forms (Last 2 Years)",
          description: "Shows your recent work history and earnings",
          fileName: "w2_forms_2022_2023.pdf",
          fileSize: 1536000,
          status: "uploaded",
          notes: "Forms from ABC Manufacturing",
          category: "employment"
        });

        await this.createDocument({
          sectionId: section.id,
          name: "Tax Returns (Last 2 Years)",
          description: "Additional proof of income and work history",
          fileName: "tax_returns_2022_2023.pdf",
          fileSize: 3072000,
          status: "uploaded",
          notes: "Filed jointly with spouse",
          category: "personal"
        });
      } else if (section.name === "Medical Evidence") {
        await this.createDocument({
          sectionId: section.id,
          name: "Primary Care Physician Records",
          description: "Records from Dr. Johnson showing ongoing treatment",
          fileName: "dr_johnson_records.pdf",
          fileSize: 4096000,
          status: "uploaded",
          contactInfo: "Dr. Johnson's office - (555) 123-4567",
          notes: "Records from 2022-2024",
          category: "medical"
        });

        await this.createDocument({
          sectionId: section.id,
          name: "Specialist Reports",
          description: "Reports from specialists who treated your condition",
          status: "missing",
          contactInfo: "Dr. Martinez's office - (555) 123-4567",
          notes: "Need orthopedic specialist reports",
          category: "medical"
        });
      } else if (section.name === "Work History Documentation") {
        await this.createDocument({
          sectionId: section.id,
          name: "Employment Records (ABC Manufacturing)",
